mapper.update:
UPDATE account
SET last_login_date = NOW()
WHERE account_id = #{accountId}

mapper.select:
SELECT a.account_id accountId FROM account a
INNER JOIN student s ON a.account_id = s.account_id WHERE s.student_name = #{memberName} AND s.student_email = #{memberEmail}
INNER JOIN teacher t ON a.account_id = t.account_id WHERE t.teacher_name = #{memberName} AND t.teacher_email = #{memberEmail}
INNER JOIN manager m ON a.account_id = m.account_id WHERE m.manager_name = #{memberName} AND m.manager_email = #{memberEmail}

SELECT COUNT(*) cnt FROM account a
INNER JOIN student s ON a.account_id = s.account_id WHERE s.account_id = #{accountId} AND s.student_name = #{memberName}
INNER JOIN teacher t ON a.account_id = t.account_id WHERE s.account_id = #{accountId} AND t.teacher_name = #{memberName}
INNER JOIN manager m ON a.account_id = m.account_id WHERE s.account_id = #{accountId} AND m.manager_name = #{memberName}

