mapper.select:
SELECT t.teacher_name teacherName , l.lecture_active lectureActive , l.lecture_name lectureName , l.lecture_start_date lectureStartDate , l.lecture_end_date lectureEndDate FROM teacher t INNER JOIN lecture l USING(teacher_name) WHERE t.account_id=#{accountId}
AND l.lecture_start_date <= CURDATE() AND l.lecture_end_date >= CURDATE()
ORDER BY l.lecture_start_date DESC

mapper.update:
UPDATE teacher
SET
teacher_name = #{teacherName}
, teacher_gender = #{teacherGender}
, teacher_birth = #{teacherBirth}
, teacher_email = #{teacherEmail}
, teacher_address = #{teacherAddress}
, teacher_detailaddress = #{teacherDetailAddress}
, teacher_phone = #{teacherPhone}
, update_date updateDate
WHERE account_id = #{accountId}
